          $self->log("Trying to connect to '$channel'\n");
          $kernel->post(
              $self->{IRCNAME},
              'join',
              $self->charset_encode($channel),
          );
      }
  
      $self->schedule_tick(5);
      $self->connected();
      return;
  }
  
