$fatpacked{"x86_64-linux/IO/Tty/Constant.pm"} = '#line '.(1+__LINE__).' "'.__FILE__."\"\n".<<'X86_64-LINUX_IO_TTY_CONSTANT';
  
  package IO::Tty::Constant;
  
  use vars qw(@ISA @EXPORT_OK);
